<?php
namespace SOSIDEE_DYNAMIC_QRCODE\SRC;
defined( 'SOSIDEE_DYNAMIC_QRCODE' ) or die( 'you were not supposed to be here' );

class Api
{
    const IMG_LOGO = 'data:image/png;base64,';

    private static function plugin() {
        return \SOSIDEE_DYNAMIC_QRCODE\SosPlugin::instance();
    }

    private static function getLogo() {
        return '<img src="' . esc_attr( self::IMG_LOGO ) . '" alt="' . esc_attr( self::plugin()->name ) . '" style="margin:1em;">';
    }

    public static function htmlCover( $url ) {
        header("Content-Type: text/html;charset=UTF-8");
        header("Refresh: 1; url=$url");

        echo '<style>body{background-color:#000000;color:#ffffff;}</style>';

        echo '<div style="display: flex; justify-content: center;">';
            echo self::getLogo();
        echo '</div>';
        echo '<div style="text-align: center;">';
            echo '<h1>' . esc_html( self::plugin()->name ) . '</h1>';
            echo '<h2><em>by SOSidee.com</em></h2>';
        echo '</div>';

    }

}